package package1;

public class Arithmetic_Operations {

 	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=100;
		int b=50;
		int add=a+b;
		int subtract=a-b;
		int div=a/b;
		int multiply=a*b;
		System.out.println(add);
		System.out.println(subtract);
		System.out.println(div);
		System.out.println(multiply);
		
	}

}
