package package1;

public class If_Else_If {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=18;
		if(a==18)
		{
			System.out.println("1");
		}
		else if(a>100)
		{
			System.out.println("2");
		}
		else if(a<100)
		{
			System.out.println("3");
		}
		else
		{
			System.out.println("4");
		}
	}

}
